ram(':city', $city);                   $leftovers->execute();				   $num = $leftovers->fetch();				   ?>				<td>		         <a  class='delete_post' href='javascript: void(0);' name='<?=$src['id']?>' title='<?=$_GET['type']?>' ><i class="fa fa-times" aria-hidden="true"></i></a>		        </td> 		      </tr>	        <? } ?> 